<?phpif (class_exists ( 'WPBakeryShortCode' )) {	class WPBakeryShortCode_propper_accordion_item extends WPBakeryShortCode {		protected function content($atts, $content = null) {			extract ( shortcode_atts ( array (					'title' => ''			), $atts ) );			ob_start();						$id = propper_randomString();						?>								<div class="panel panel-default">				<div class="panel-heading" role="tab">					<h4 class="panel-title">						<a role="button" data-toggle="collapse" href="#<?php echo $id; ?>" aria-expanded="true" aria-controls="<?php echo $id; ?>"><?php echo esc_attr ( $title ); ?></a>					</h4>				</div>				<div id="<?php echo $id; ?>" class="panel-collapse collapse" role="tabpanel">					<div class="panel-body">						<?php echo $content;?>					</div>				</div>			</div>			<?php											$output = ob_get_clean(); 			return $output;		}	}}