<?phpif ( class_exists( 'WPBakeryShortCodesContainer' ) ) {	class WPBakeryShortCode_propper_faq_section extends WPBakeryShortCodesContainer {		protected function content($atts, $content = null) {						extract ( shortcode_atts ( array (			                'parent_id' => '',							), $atts ) );			ob_start();						?>				<div class="panel-group background-solid" id="<?php echo esc_attr($parent_id);?>" aria-multiselectable="true" data-scroll-reveal="enter left and move 20px after">					<?php echo do_shortcode($content); ?>										</div>			<?php 					$output = ob_get_clean();			return $output;		}	}}