<?phpclass propper_comment {	// /Comment call back function	static function propper_comments($comment, $args, $depth) {		$GLOBALS ['comment'] = $comment;		extract ( $args, EXTR_SKIP );		if ('div' == $args ['style']) {			$tag = 'div';			$add_below = 'comment';		} else {			$tag = 'li';			$add_below = 'div-comment';		}		?>		<<?php echo esc_attr($tag); ?> <?php comment_class ( 'comment' )?>>		<div id="div-comment">						<div class="pull-left">				<?php if ( $args['avatar_size'] != 0 ) echo get_avatar( $comment, $args['avatar_size'] ); ?>			</div>									<div class="comment-body">											<?php comment_reply_link( array_merge( $args, array( 'add_below' => $add_below, 'depth' => $depth, 'max_depth' => $args['max_depth'], 'reply_text '=>'Reply','login_text'=>'Log In' ) ) ); ?>								<h4 class="comment-heading"><?php echo esc_attr($comment->comment_author);?></h4>				<p class="comment-time"><?php echo date(get_option( 'date_format' ), strtotime($comment->comment_date)) ?></p>							<p>	<?php echo get_comment_text(); ?>	</p>						</div>					<div class="hidden"><?php 			comment_form(); 					paginate_comments_links() 		?></div></div><?php	}}